 };
return (
  <>
    <div className="container">
       <h3>CRUD Application</h3>
       <div className="input-search">
      <input type="search" placeholder="Search Text Here" onChange={handleSearchChange}/>
     <button className="btn green">Add Record</button>

<table className="table">
 <thead>
   <tr>
       <th>Id</th>
       <th>Name</th>
       <th>Age</th>
       <th>City</th>
       <th>Edit</th>
      <th>Delete</th>
   </tr>
 </thead>
 <tbody>
 {users && 
    users.map((user, index)=> {
    return(
        <tr key={user.id}>
           <td>{index + 1}</td>
           <td>{user.name}</td>
           <td>{user.age}</td>
           <td>{user.city}</td>
        <td>
          <button className="btn green">Edit</button></td>
          <td><button className="btn red">Delete</button></td>
          </tr>         
  );
 })}
    </tbody>
 </table>
</div>
</div>
</>
);
}
export default App;
